{"changed":true,"filter":false,"title":"send.php","tooltip":"/laravel/public/send.php","value":"<?php\n  // load configuration for database\n  require_once('config.inc.php'); \n  // connect to database\n  $mysqli = new mysqli($database_host, $database_user, $database_password, $database_name); \n\n  $content = $_POST[\"content\"];\n\n  // pickup cookies\n  $id= $_COOKIE[\"id\"];\n  $firstname= $_COOKIE[\"firstname\"];\n  $lastname= $_COOKIE[\"lastname\"];\n  if (isset($_COOKIE[\"language\"])){\n    setcookie(\"language\",\"en\");\n  } // if language cookie\n  $language = $_COOKIE[\"language\"];\n  \n  $partnerID= $_COOKIE[\"partnerID\"];\n  $partnerfirstname= $_COOKIE[\"partnerfirstname\"];\n  $partnerlastname = $_COOKIE[\"partnerlastname\"];\n  $partnerlanguage = $_COOKIE[\"partnerlanguage\"];\n  echo($partnerlanguage);\n  $time = time();\n  setcookie(\"noclear\", \"whocares\", time()+1);\n  \n  \n  \n  //Translate the message\n  $translatedcontent = translate($language, $partnerlanguage, $content);\n  \n  \n  \n  $insert_row = $mysqli->query(\"INSERT INTO exchanges (senderID, receiverID, time, sender_content, receiver_content) \n\t\t\t\t    VALUES  ('$id', '$partnerID', '$time', '$content', '$translatedcontent')\");\n\t\t\t\t    \n\t function url_get_contents ($Url) {\n      if (!function_exists('curl_init')){ \n          die('CURL is not installed!');\n      }\n      $ch = curl_init();\n      curl_setopt($ch, CURLOPT_URL, $Url);\n      curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n      $output = curl_exec($ch);\n      curl_close($ch);\n      return $output;\n   }       \n  \n  \n   function translate($senderlang, $recieverlang, $message)\n   {\n     // Add the plus signs inside the message so it can be send via api request\n     $newstring = implode(\"+\", preg_split(\"/[\\s]+/\", $message));\n     \n     // Call the API\n     $url = \"http://itranslate4.eu/api/Translate?auth=785f2f42-eab1-461d-8a72-a1867112458a&src=\" . $senderlang . \"&trg=\" . $recieverlang . \"&dat=\" . $newstring;\n     // Get the contents of the URL\n     $contents = url_get_contents($url);\n     $echo $url; \n     $echo \"<br>\";\n     $echo $senderlang;\n          $echo \"<br>\";\n\n     $echo $recieverlang;\n          $echo \"<br>\";\n\n     $echo $contents;\n     // Decode the returned JSON object and get the text\n     $json = json_decode($contents, true);\n     $translation = $json['dat'][0]['text'][0];\n     echo \"<br>\";\n     return $translation;\n     \n     \n   }\n      \n   //header(\"Location: https://web.cs.manchester.ac.uk/mbax4ab4/babeltalk/laravel/public/testinput.php\");\n  \n?>","undoManager":{"mark":97,"position":100,"stack":[[{"group":"doc","deltas":[{"start":{"row":33,"column":11},"end":{"row":33,"column":12},"action":"insert","lines":["t"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":12},"end":{"row":33,"column":13},"action":"insert","lines":["n"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":13},"end":{"row":33,"column":14},"action":"insert","lines":["e"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":14},"end":{"row":33,"column":15},"action":"insert","lines":["r"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":15},"end":{"row":33,"column":16},"action":"insert","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":15},"end":{"row":33,"column":16},"action":"remove","lines":["i"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":15},"end":{"row":33,"column":16},"action":"insert","lines":["I"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":16},"end":{"row":33,"column":17},"action":"insert","lines":["D"]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":17},"end":{"row":34,"column":0},"action":"insert","lines":["",""]},{"start":{"row":34,"column":0},"end":{"row":34,"column":2},"action":"insert","lines":["  "]}]}],[{"group":"doc","deltas":[{"start":{"row":33,"column":17},"end":{"row":34,"column":0},"action":"insert","lines":["",""]},{"s